<template>    <div>        <modal :size="'md'" :title="title" :footer="false" id="modal-demo-1" ref="modal1" v-model="openModalInside"  @show="showCallback" @hide="dismissCallback">            <div class="wrapper-alert">                <alert type="danger" :closable="true" v-if="showAlert" @close="showAlert=false">                    <h4><i class="icon fa fa-ban"></i> Error!</h4>                    <p>{{ errMsg }}</p>                </alert>                <alert type="success" :closable="true" v-if="showSuccess" @close="showSuccess=false">                    <h4><i class="icon fa fa-check"></i> Exito!</h4>                    <p>{{ successMsg }}</p>                </alert>            </div>            <div class="box box-primary">                            <label>Categoria: {{ updateCategoria.nombreCategoria }}</label>                <form @submit.prevent="validateMethod" role="form">                     <div class="box-body">                       <input type="hidden" v-model="id">                         <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('nombreCategoria') }" >                                <label for="">Nombre Categoria</label>                                <input type="text" id="nombreCategoria" name="nombreCategoria" data-vv-as="Nombre de la Categoria" class="form-control" v-model="updateCategoria.nombreCategoria" v-validate="'required'" >                                <span class="help-block" for="nombreCategoria" v-bind:data-error="errors.first('nombreCategoria')">                                    {{ errors.first('nombreCategoria') }}                                </span>                            </div>                        </div>                        <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('edadMin') }" >                                <label for="">Edad Minima</label>                                <input type="number" id="edadMin" name="edadMin" data-vv-as="Edad minima de la categoria" class="form-control" v-model="updateCategoria.edadMin" v-validate="'required|min_value:7|max_value:79'" >                                <span class="help-block" for="edadMin" v-bind:data-error="errors.first('edadMin')">                                    {{ errors.first('edadMin') }}                                </span>                            </div>                        </div>                         <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('edadMax') }" >                                <label for="">Edad Maxima</label>                                <input type="number" id="edadMax" name="edadMax" data-vv-as="Edad maxima de la categoria" class="form-control" v-model="updateCategoria.edadMax" v-validate="'required|min_value:7|max_value:79'" >                                <span class="help-block" for="edadMax" v-bind:data-error="errors.first('edadMax')">                                    {{ errors.first('edadMax') }}                                </span>                            </div>                        </div>                                                                                          <div class="col-xs-12 col-sm-6">                                        <div class="fgroup"  :class="{ 'has-error': errors.has('sexo') }">                                            <label for="">Sexo</label>                                            <v-select                                                :debounce="250"                                                :options="sexo"                                                v-model="updateCategoria.sexo"                                                placeholder="Seleccione el genero"                                                 label="nombre">                                            </v-select>                                            <div class="clearfix"></div>                                            <input type="hidden" name="sexo" value="" data-vv-as="sexo"  v-model="updateCategoria.sexo" v-validate="'required'">                                            <span class="help-block" for="sexo" v-bind:data-error="errors.first('sexo')">                                                {{ errors.first('sexo') }}                                            </span>                                        </div>                                    </div>                        <div class="col-xs-12 col-sm-6">                                        <div class="fgroup" :class="{ 'has-error': errors.has('descripcionCategoria') }" >                                            <label for="">Descripci√≥n</label>                                            <textarea rows="4" cols="50" id="descripcionCategoria" name="descripcionCategoria" data-vv-as="Descripcion de la categoria" class="form-control" v-model="updateCategoria.descripcionCategoria" v-validate="'required'"> </textarea>                                        <span class="help-block" for="descripcionCategoria" v-bind:data-error="errors.first('descripcionCategoria')">                                                {{ errors.first('descripcionCategoria') }}                                            </span>                                        </div>                                    </div>                                                                </div>                        <div class="box-footer">                        <div class="col-xs-12 text-right">                            <button type="submit" class="btn btn-flat btn-sm btn-primary"><i class="fa fa-edit"></i> {{ buttonMsg }}</button>                        </div>                    </div>                </form>            </div>        </modal>    </div></template><script>    import users from '../../../controllers/users.js'    import roles from '../../../controllers/roles.js'    import categorias from '../../../controllers/categorias.js'    import moment from 'moment'    import vSelect from "vue-select"    export default {        name: 'ActualizarCategoria',        props:  [                        "title",            "id",            "buttonMsg",            "methodSubmit",            "openModal",            "categoria"        ],        computed: {        },        created(){            this.retrieveRoles()        },        data() {            return {                openValue: "",                errMsg:  '',                successMsg:  '',                success: false,                isLogin: false,                show: false,                showAlert: false,                showSuccess: false,                updateCategoria: {},                openModalInside: false,                roles: [],                rol_edit: null,                 sexo:[                    {name: 'M', nombre:'Masculino'},                    {name: 'F', nombre:'Femenino'},                    {name: 'X', nombre:'Mixto'}                                                      ]            }        },        components:{            vSelect        },        watch: {            '$route': 'fetchData',            categoria: function(val, oldVal){                this.openModalInside = this.openModal                this.updateCategoria = this.categoria            },            updateCategoria(){                if(typeof this.updateCategoria.sexo != 'object' ){                    let nombre = this.updateCategoria.sexo == 'M' ? 'Masculino' : this.updateCategoria.sexo == 'F' ? 'Femenino' : 'Mixto'                    let name = this.updateCategoria.sexo                     this.updateCategoria.sexo = this.updateCategoria.sexo.replace(/\s/g, "")                    this.updateCategoria.sexo = {name: name, nombre: nombre}                   }                            }        },        methods:{            showCallback () {            },            dismissCallback (msg) {                this.showSuccess = false                this.showAlert = false                this.openModalInside =false                this.$emit('openChange', false);            },            retrieveRoles(){                roles.index(this)            },            retrieveData(id) {                categorias.retrieve(this, id)            },            validateMethod(){                console.log(this.methodSubmit)                if(this.methodSubmit == 'update'){                    this.updateUsuario();                }                else{                    console.log(this.methodSubmit)                }            },            updateUsuario() {                this.showAlert = false                this.showSuccess = false                this.$validator.validateAll().then(success => {                    if (success) {                                               if( this.updateCategoria.sexo=='Masculino'){                        this.updateCategoria.sexo='M'                      }                       if( this.updateCategoria.sexo=='Femenino'){                        this.updateCategoria.sexo='F'                      }                        if( this.updateCategoria.sexo=='Mixto'){                        this.updateCategoria.sexo='X'                      }                        let updateCategoria = this.updateCategoria                        updateCategoria.sexo = updateCategoria.sexo.name == undefined ? this.updateCategoria.sexo : updateCategoria.sexo.name                        categorias.update(this, updateCategoria)                    }                    else{                        this.errMsg= 'Error revisa el formulario'                        this.showAlert=true                    }                })            }        }    }</script>