<template>    <div>        <modal :size="'md'" :title="title" :footer="false" id="modal-demo-1" ref="modal1" v-model="openModalInside"  @show="showCallback" @hide="dismissCallback">            <div class="wrapper-alert">                <alert type="danger" :closable="true" v-if="showAlert" @close="showAlert=false">                    <h4><i class="icon fa fa-ban"></i> Error!</h4>                    <p>{{ errMsg }}</p>                </alert>                <alert type="success" :closable="true" v-if="showSuccess" @close="showSuccess=false">                    <h4><i class="icon fa fa-check"></i> Exito!</h4>                    <p>{{ successMsg }}</p>                </alert>            </div>            <div class="box box-primary">                            <label>Cuenta: {{ updateCuenta.nombre }}</label>                <form @submit.prevent="validateMethod" role="form">                     <div class="box-body">                       <input type="hidden" v-model="id">                         <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('nombre') }" >                                <label for="">Nombre </label>                                <input type="text" id="nombre" name="nombre" data-vv-as="Nombre de la Cuenta" class="form-control" v-model="updateCuenta.nombre" v-validate="'required'" >                                <span class="help-block" for="nombre" v-bind:data-error="errors.first('nombre')">                                    {{ errors.first('nombre') }}                                </span>                            </div>                        </div>                       <!-- <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('cuentaMayor') }" >                                <label for="">Cuenta Mayor</label>                                <input type="number" id="cuentaMayor" name="cuentaMayor" data-vv-as="Cuenta Mayor de la cuenta" class="form-control" v-model="updateCuenta.cuentaMayor" v-validate="'required'" >                                <span class="help-block" for="cuentaMayor" v-bind:data-error="errors.first('cuentaMayor')">                                    {{ errors.first('cuentaMayor') }}                                </span>                            </div>                        </div> -->                                                <!--  <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('naturaleza') }" >                                <label for="">Naturaleza </label>                                <input type="text" id="naturaleza" name="naturaleza" data-vv-as="Naturaleza de la Cuenta" class="form-control" v-model="updateCuenta.naturaleza" v-validate="'required'" >                                <span class="help-block" for="naturaleza" v-bind:data-error="errors.first('naturaleza')">                                    {{ errors.first('naturaleza') }}                                </span>                            </div>                        </div> -->                        <div class="col-xs-12 col-sm-4">                                        <div class="fgroup"  :class="{ 'has-error': errors.has('naturaleza') }">                                            <label for="">Naturaleza de la Cuenta</label>                                            <v-select                                                :debounce="250"                                                :options="naturalezas"                                                v-model="updateCuenta.naturaleza"                                                placeholder="Seleccione la naturaleza de la Cuenta"                                                label="name">                                            </v-select>                                            <div class="clearfix"></div>                                            <input type="hidden" name="naturaleza" value="" data-vv-as="Naturaleza de la Cuenta"  v-model="updateCuenta.naturaleza" v-validate="'required'">                                            <span class="help-block" for="naturaleza" v-bind:data-error="errors.first('naturaleza')">                                                {{ errors.first('naturaleza') }}                                            </span>                                        </div>                        </div>                                                                                              </div>                        <div class="box-footer">                        <div class="col-xs-12 text-right">                            <button type="submit" class="btn btn-flat btn-sm btn-primary">{{ buttonMsg }}</button>                        </div>                    </div>                </form>            </div>        </modal>    </div></template><script>    import users from '../../../controllers/users.js'    import roles from '../../../controllers/roles.js'    import cuentas from '../../../controllers/cuentas.js'    import moment from 'moment'    import vSelect from "vue-select"    export default {        name: 'ActualizarCuenta',        props:  [                        "title",            "id",            "buttonMsg",            "methodSubmit",            "openModal",            "cuenta"        ],        computed: {        },        created(){            this.retrieveRoles()        },        data() {            return {                openValue: "",                errMsg:  '',                successMsg:  '',                success: false,                isLogin: false,                show: false,                showAlert: false,                showSuccess: false,                updateCuenta: {},                openModalInside: false,                roles: [],                rol_edit: null,                naturalezas: [                    {name: 'Pasivo'},                    {name: 'Activo'}]            }        },        components:{            vSelect        },        watch: {            '$route': 'fetchData',            cuenta: function(val, oldVal){                this.openModalInside = this.openModal                this.updateCuenta = this.cuenta            }                    },        methods:{            showCallback () {            },            dismissCallback (msg) {                this.showSuccess = false                this.showAlert = false                this.openModalInside =false                this.$emit('openChange', false);            },            retrieveRoles(){                roles.index(this)            },            retrieveData(id) {                cuentas.retrieve(this, id)            },            validateMethod(){                console.log(this.methodSubmit)                if(this.methodSubmit == 'update'){                    this.updateUsuario();                }                else{                    console.log(this.methodSubmit)                }            },            updateUsuario() {                this.showAlert = false                this.showSuccess = false                this.$validator.validateAll().then(success => {                    if (success) {                        this.updateCuenta.naturaleza = this.updateCuenta.naturaleza.name == undefined ? '' : this.updateCuenta.naturaleza.name                        cuentas.update(this, this.updateCuenta)                    }                    else{                        this.errMsg= 'Error revisa el formulario'                        this.showAlert=true                    }                })            }        }    }</script>