  <template>    <div>        <modal :size="'md'" :title="title" :footer="false" id="modal-demo-1" ref="modal1" v-model="openModalInside"  @show="showCallback" @hide="dismissCallback">            <div class="wrapper-alert">                <alert type="danger" :closable="true" v-if="showAlert" @close="showAlert=false">                    <h4><i class="icon fa fa-ban"></i> Error!</h4>                    <p>{{ errMsg }}</p>                </alert>                <alert type="success" :closable="true" v-if="showSuccess" @close="showSuccess=false">                    <h4><i class="icon fa fa-check"></i> Exito!</h4>                    <p>{{ successMsg }}</p>                </alert>            </div>            <div class="box box-primary">                            <label>Titulo {{ updateEscuelas.entidadId }}</label>                <form @submit.prevent="validateMethod" role="form">                     <div class="box-body">                       <input type="hidden" v-model="id">                         <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('nombre') }" >                                <label for="">Entidad</label>                                <input type="text" id="entidadId" name="entidadId" data-vv-as="Escuela" class="form-control" v-model="updateEscuelas.entidadId" v-validate="'required'" >                                <span class="help-block" for="nombre" v-bind:data-error="errors.first('nombre')">                                    {{ errors.first('entidadId') }}                                </span>                            </div>                        </div>                        <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('descripcion') }" >                                <label for="">Fundacion</label>                                <input type="date" id="fundacion" name="fundacion" data-vv-as="Fundacion" class="form-control" v-model="updateEscuelas.fundacion" v-validate="'required'" >                                <span class="help-block" for="descripcion" v-bind:data-error="errors.first('descripcion')">                                    {{ errors.first('fundacion') }}                                </span>                            </div>                        </div>                         <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('fechaInicio') }" >                                <label for="">Playa</label>                                <input type="text" id="playaId" name="playaId" data-vv-as="Playa" class="form-control" v-model="updateEscuelas.playaId" v-validate="'required'" >                                <span class="help-block" for="fechaInicio" v-bind:data-error="errors.first('fechaInicio')">                                    {{ errors.first('playaId') }}                                </span>                            </div>                        </div>                         <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('fechaFin') }" >                                <label for="">Entrenador</label>                                <input type="text" id="entrenadorId" name="entrenadorId" data-vv-as="Entrenador" class="form-control" v-model="updateEscuelas.entrenadorId" v-validate="'required'" >                                <span class="help-block" for="fechaFin" v-bind:data-error="errors.first('fechaFin')">                                    {{ errors.first('entrenadorId') }}                                </span>                            </div>                        </div>                                                                </div>                        <div class="box-footer">                        <div class="col-xs-12 text-right">                            <button type="submit" class="btn btn-flat btn-sm btn-primary">{{ buttonMsg }}</button>                        </div>                    </div>                </form>            </div>        </modal>    </div></template><script>    import users from '../../../controllers/users.js'    import roles from '../../../controllers/roles.js'    import escuelas from '../../../controllers/escuelas.js'    import moment from 'moment'    import vSelect from "vue-select"    export default {        name: 'ActualizarEscuela',        props:  [                        "title",            "id",            "buttonMsg",            "methodSubmit",            "openModal",            "escuela"        ],        computed: {        },        created(){            this.retrieveRoles()        },        data() {            return {                openValue: "",                errMsg:  '',                successMsg:  '',                success: false,                isLogin: false,                show: false,                showAlert: false,                showSuccess: false,                updateEscuela: {},                openModalInside: false,                roles: [],                rol_edit: null            }        },        components:{            vSelect        },        watch: {            '$route': 'fetchData',            noticia: function(val, oldVal){                this.openModalInside = this.openModal                this.updateEscuela = this.escuela                            }        },        methods:{            showCallback () {            },            dismissCallback (msg) {                this.showSuccess = false                this.showAlert = false                this.openModalInside =false                this.$emit('openChange', false);            },            retrieveRoles(){                roles.index(this)            },            retrieveData(id) {                escuelas.retrieve(this, id)            },            validateMethod(){                console.log(this.methodSubmit)                if(this.methodSubmit == 'update'){                    this.updateUsuario();                }                else{                    console.log(this.methodSubmit)                }            },            updateEscuela() {                this.showAlert = false                this.showSuccess = false                this.$validator.validateAll().then(success => {                    if (success) {                        escuelas.update(this, this.updateEscuela)                    }                    else{                        this.errMsg= 'Error revisa el formulario'                        this.showAlert=true                    }                })            }        }    }</script>