<template>    <div>        <modal :size="'md'" :title="title" :footer="false" id="modal-demo-1" ref="modal1" v-model="openModalInside"  @show="showCallback" @hide="dismissCallback">            <div class="wrapper-alert">                <alert type="danger" :closable="true" v-if="showAlert" @close="showAlert=false">                    <h4><i class="icon fa fa-ban"></i> Error!</h4>                    <p>{{ errMsg }}</p>                </alert>                <alert type="success" :closable="true" v-if="showSuccess" @close="showSuccess=false">                    <h4><i class="icon fa fa-check"></i> Exito!</h4>                    <p>{{ successMsg }}</p>                </alert>            </div>            <div class="box box-primary" >                            <label>Descripcion: {{ updateJuez.descripcion}}</label>                <form @submit.prevent="validateMethod" role="form">                     <div class="box-body">                       <input type="hidden" v-model="id">                         <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('nombre') }" >                                <label for="">Nombre</label>                                <input type="text" id="nombre" name="nombre" data-vv-as="Nombre" class="form-control"                                 v-model="updateJuez.personaId.nombre" v-validate="'required'" >                                <span class="help-block" for="nombre" v-bind:data-error="errors.first('nombre')">                                    {{ errors.first('nombre') }}                                </span>                            </div>                        </div>                                              <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('apellido') }" >                                <label for="">Apellido</label>                                <input type="text" id="apellido" name="apellido" data-vv-as="Apellido" class="form-control" v-model="updateJuez.personaId.apellido" v-validate="'required'" >                                <span class="help-block" for="apellido" v-bind:data-error="errors.first('apellido')">                                    {{ errors.first('apellido') }}                                </span>                            </div>                        </div>                         <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('fechaNacimiento') }" >                                <label for="">Fecha de Nacimiento</label>                                <input type="date" id="fechaNacimiento" name="fechaNacimiento" data-vv-as="Fecha de Nacimiento" class="form-control" v-model="updateJuez.personaId.fechaNacimiento" v-validate="'required'">                                <span class="help-block" for="fechaNacimiento" v-bind:data-error="errors.first('fechaNacimiento')">                                    {{ errors.first('fechaNacimiento') }}                                </span>                            </div>                        </div>                         <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('dui') }" >                                <label for="">Dui</label>                                <input type="text" id="dui" name="dui" data-vv-as="Dui" class="form-control" v-model="updateJuez.personaId.dui" v-validate="'required'" >                                <span class="help-block" for="dui" v-bind:data-error="errors.first('dui')">                                    {{ errors.first('dui') }}                                </span>                            </div>                        </div>                        <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('nit') }" >                                <label for="">Nit</label>                                <input type="text" id="nit" name="nit" data-vv-as="Nit" class="form-control" v-model="updateJuez.personaId.nit" v-validate="'required'" >                                <span class="help-block" for="nit" v-bind:data-error="errors.first('nit')">                                    {{ errors.first('nit') }}                                </span>                            </div>                        </div>                         <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('direccion') }" >                                <label for="">Direccion</label>                                <input type="text" id="direccion" name="direccion" data-vv-as="Direccion" class="form-control" v-model="updateJuez.personaId.direccion" v-validate="'required'" >                                <span class="help-block" for="nit" v-bind:data-error="errors.first('direccion')">                                    {{ errors.first('direccion') }}                                </span>                            </div>                        </div>                        <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('telefono') }" >                                <label for="">Telefono</label>                                <input type="text" id="telefono" name="telefono" data-vv-as="Telefono" class="form-control" v-model="updateJuez.personaId.telefono" v-validate="'required'" >                                <span class="help-block" for="nit" v-bind:data-error="errors.first('telefono')">                                    {{ errors.first('telefono') }}                                </span>                            </div>                        </div>                         <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('correo') }" >                                <label for="">Correo</label>                                <input type="text" id="correo" name="correo" data-vv-as="Correo" class="form-control" v-model="updateJuez.personaId.correo" v-validate="'required'" >                                <span class="help-block" for="nit" v-bind:data-error="errors.first('correo')">                                    {{ errors.first('correo') }}                                </span>                            </div>                        </div>                        <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('descripcion') }" >                                <label for="">Descripcion</label>                                <input type="text" id="descripcion" name="descripcion" data-vv-as="Descripcion" class="form-control" v-model="updateJuez.descripcion" v-validate="'required'" >                                <span class="help-block" for="descripcion" v-bind:data-error="errors.first('descripcion')">                                    {{ errors.first('descripcion') }}                                </span>                            </div>                        </div>                                                                                      </div>                        <div class="box-footer">                        <div class="col-xs-12 text-right">                            <button type="submit" class="btn btn-flat btn-sm btn-primary">{{ buttonMsg }}</button>                        </div>                    </div>                </form>            </div>        </modal>    </div></template><script>    import users from '../../../controllers/users.js'    import roles from '../../../controllers/roles.js'    import jueces from '../../../controllers/jueces.js'    import moment from 'moment'    import vSelect from "vue-select"    export default {        name: 'ActualizarEntrenador',        props:  [                        "title",            "id",            "buttonMsg",            "methodSubmit",            "openModal",            "juez"        ],        computed: {        },        created(){            this.retrieveRoles()        },        data() {            return {                openValue: "",                errMsg:  '',                successMsg:  '',                success: false,                isLogin: false,                show: false,                showAlert: false,                showSuccess: false,                updateJuez: {personaId:' '},                openModalInside: false,                roles: [],                rol_edit: null            }        },        components:{            vSelect        },        watch: {            '$route': 'fetchData',            juez: function(val, oldVal){                this.openModalInside = this.openModal                this.updateJuez = this.juez                            }        },        methods:{            showCallback () {            },            dismissCallback (msg) {                this.showSuccess = false                this.showAlert = false                this.openModalInside =false                this.$emit('openChange', false);            },            retrieveRoles(){                roles.index(this)            },            retrieveData(id) {                jueces.retrieve(this, id)            },            validateMethod(){                console.log(this.methodSubmit)                if(this.methodSubmit == 'update'){                    this.updateUsuario();                }                else{                    console.log(this.methodSubmit)                }            },            updateUsuario() {                this.showAlert = false                this.showSuccess = false                this.$validator.validateAll().then(success => {                    if (success) {                        jueces.update(this, this.updateJuez)                    }                    else{                        this.errMsg= 'Error revisa el formulario'                        this.showAlert=true                    }                })            }        }    }</script>