<template>    <div>        <modal :size="'md'" :title="title" :footer="false" id="modal-demo-1" ref="modal1" v-model="openModalInside"  @show="showCallback" @hide="dismissCallback">            <div class="wrapper-alert">                <alert type="danger" :closable="true" v-if="showAlert" @close="showAlert=false">                    <h4><i class="icon fa fa-ban"></i> Error!</h4>                    <p>{{ errMsg }}</p>                </alert>                <alert type="success" :closable="true" v-if="showSuccess" @close="showSuccess=false">                    <h4><i class="icon fa fa-check"></i> Exito!</h4>                    <p>{{ successMsg }}</p>                </alert>            </div>            <div class="box box-primary">                            <label>Usuario {{ updateUser.nombre }}</label>                <form @submit.prevent="validateMethod" role="form">                     <div class="box-body">                       <<input type="hidden" v-model="id">                         <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('nombre') }" >                                <label for="">Nombre</label>                                <input type="text" id="nombre" name="nombre" data-vv-as="Titulo de la Noticia" class="form-control" v-model="updateUser.nombre" v-validate="'required'" >                                <span class="help-block" for="nombre" v-bind:data-error="errors.first('nombre')">                                    {{ errors.first('nombre') }}                                </span>                            </div>                        </div>                     <!--   <div class="col-xs-12 col-sm-6">                            <div class="fgroup"  :class="{ 'has-error': errors.has('rol_edit') }">                                <label for="">Position</label>                                <v-select                                    :debounce="250"                                    :options="roles"                                    v-model="updateUser.rolId"                                    placeholder="Rol Usuario"                                     label="nombre">                                </v-select>                                <div class="clearfix"></div>                                <input type="hidden" name="rol_edit" value="" data-vv-as="Position Employee"  v-model="updateUser.rolId" v-validate="'required'">                                <span class="help-block" for="rol_edit" v-bind:data-error="errors.first('rol_edit')">                                    {{ errors.first('rol_edit') }}                                </span>                            </div>                        </div>   -->                                            </div>                        <div class="box-footer">                        <div class="col-xs-12 text-right">                            <button type="submit" class="btn btn-flat btn-sm btn-primary">{{ buttonMsg }}</button>                        </div>                    </div>                </form>            </div>        </modal>    </div></template><script>    import users from '../../../controllers/users.js'    import roles from '../../../controllers/roles.js'    import noticias from '../../../controllers/noticias.js'    import moment from 'moment'    import vSelect from "vue-select"    export default {        name: 'Employee',        props:  [           /*"usuario",                       "usuario",            "contrasena", */             "title",            "id",            "buttonMsg",            "methodSubmit",            "openModal",            "noticias"        ],        computed: {        },        created(){            this.retrieveRoles()        },        data() {            return {                openValue: "",                errMsg:  '',                successMsg:  '',                success: false,                isLogin: false,                show: false,                showAlert: false,                showSuccess: false,                updateUser: {},                openModalInside: false,                roles: [],                rol_edit: null            }        },        components:{            vSelect        },        watch: {            '$route': 'fetchData',            noticias: function(val, oldVal){                this.openModalInside = this.openModal                this.updateUser = this.noticias                /*this.updateUser.rolId = this.usuario.rolId */            }        },        methods:{            showCallback () {            },            dismissCallback (msg) {                this.showSuccess = false                this.showAlert = false                this.openModalInside =false                this.$emit('openChange', false);            },            retrieveRoles(){                roles.index(this)            },            retrieveData(id) {                noticias.retrieve(this, id)            },            validateMethod(){                console.log(this.methodSubmit)                if(this.methodSubmit == 'update'){                    this.updateUsuario();                }                else{                    console.log(this.methodSubmit)                }            },            updateUsuario() {                this.showAlert = false                this.showSuccess = false                this.$validator.validateAll().then(success => {                    if (success) {                        noticias.update(this, this.updateUser)                    }                    else{                        this.errMsg= 'Error revisa el formulario'                        this.showAlert=true                    }                })            }        }    }</script>