<template>    <div>        <modal :size="'md'" :title="title" :footer="false" id="modal-demo-1" ref="modal1" v-model="openModalInside"  @show="showCallback" @hide="dismissCallback">            <div class="wrapper-alert">                <alert type="danger" :closable="true" v-if="showAlert" @close="showAlert=false">                    <h4><i class="icon fa fa-ban"></i> Error!</h4>                    <p>{{ errMsg }}</p>                </alert>                <alert type="success" :closable="true" v-if="showSuccess" @close="showSuccess=false">                    <h4><i class="icon fa fa-check"></i> Exito!</h4>                    <p>{{ successMsg }}</p>                </alert>            </div>            <div class="box box-primary">                            <label>Título: {{ updateNoticia.nombre }}</label>                <form @submit.prevent="validateMethod" role="form">                     <div class="box-body">                       <input type="hidden" v-model="id">                         <div class="col-xs-12 col-sm-12">                            <div class="fgroup" :class="{ 'has-error': errors.has('nombre') }" >                                <label for="">Título</label>                                <input type="text" id="nombre" name="nombre" data-vv-as="Titulo de la Noticia" class="form-control" v-model="updateNoticia.nombre" v-validate="'required'" >                                <span class="help-block" for="nombre" v-bind:data-error="errors.first('nombre')">                                    {{ errors.first('nombre') }}                                </span>                            </div>                        </div>                                                                        <div class="col-xs-12 col-sm-6">                                        <div class="fgroup" :class="{ 'has-error': errors.has('fechaInicio') }" >                                            <label for="">Fecha Inicio</label>                                            <dropdown class="form-group">                                                <div class="input-group">                                                    <input placeholder="AAAA-MM-DD" type="text" id="fechaInicio" name="fechaInicio" class="form-control" data-vv-as="Fecha Inicio" v-model="updateNoticia.fechaInicio" v-validate="'required|date_format:YYYY-MM-DD'" :class="{'datepicker':true,  'has-error': errors.has('fechaInicio') }">                                                    <div class="input-group-btn">                                                        <button class="btn btn-default" type="button" data-role="trigger" :class="{'has-error': errors.has('fechaInicio') }">                                                            <i class="glyphicon glyphicon-calendar"></i>                                                        </button>                                                    </div>                                                </div>                                                <template slot="dropdown">                                                    <li>                                                        <date-picker class="date-picker" :language="'es'" v-model="updateNoticia.fechaInicio" :today-btn="todayBtn" :clear-btn="clearBtn" :limit-from="limitFrom" :format="format" :week-starts-with="weekStartsWith" :limit-to="limitTo" :close-on-selected="closeOnSelected">                                                        </date-picker>                                                    </li>                                                </template>                                            </dropdown>                                            <span class="help-block" for="FechaInicio" v-bind:data-error="errors.first('fechaInicio')">                                                {{ errors.first('fechaInicio') }}                                            </span>                                        </div>                                    </div>                         <div class="col-xs-12 col-sm-6">                                        <div class="fgroup" :class="{ 'has-error': errors.has('fechaFin') }" >                                            <label for="">Fecha Fin</label>                                            <dropdown class="form-group">                                                <div class="input-group">                                                    <input placeholder="AAAA-MM-DD" type="text" id="fechaFin" name="fechaFin" class="form-control" data-vv-as="Fecha Fin" v-model="updateNoticia.fechaFin" v-validate="'required|date_format:YYYY-MM-DD|after:fechaInicio'" :class="{'datepicker':true,  'has-error': errors.has('fechaFin') }">                                                    <div class="input-group-btn">                                                        <button class="btn btn-default" type="button" data-role="trigger" :class="{'has-error': errors.has('fechaFin') }">                                                            <i class="glyphicon glyphicon-calendar"></i>                                                        </button>                                                    </div>                                                </div>                                                <template slot="dropdown">                                                    <li>                                                        <date-picker class="date-picker" :language="'es'" v-model="updateNoticia.fechaFin" :today-btn="todayBtn" :clear-btn="clearBtn" :limit-from="limitFrom" :format="format" :week-starts-with="weekStartsWith" :limit-to="limitTo" :close-on-selected="closeOnSelected">                                                        </date-picker>                                                    </li>                                                </template>                                            </dropdown>                                             <span class="help-block" for="fechaFin" v-bind:data-error="errors.first('fechaFin')">                                                {{ errors.first('fechaFin') }}                                            </span>                                        </div>                                    </div>                                    <div class="col-xs-12 col-sm-12">                                        <div class="fgroup" :class="{ 'has-error': errors.has('descripcion') }" >                                            <label for="">Descripción</label>                                            <textarea id="descripcion" name="descripcion" data-vv-as="Descripcion de la noticia" class="form-control" v-model="updateNoticia.descripcion" v-validate="'required'" rows="3"></textarea>                                            <span class="help-block" for="descripcion" v-bind:data-error="errors.first('descripcion')">                                                 {{ errors.first('descripcion') }}                                            </span>                                        </div>                                    </div>                                                                                         </div>                        <div class="box-footer">                        <div class="col-xs-12 text-right">                            <button type="submit" class="btn btn-flat btn-sm btn-primary">{{ buttonMsg }}</button>                        </div>                    </div>                </form>            </div>        </modal>    </div></template><script>    import users from '../../../controllers/users.js'    import roles from '../../../controllers/roles.js'    import noticias from '../../../controllers/noticias.js'    import moment from 'moment'    import vSelect from "vue-select"    export default {        name: 'ActualizarNoticia',        props:  [                        "title",            "id",            "buttonMsg",            "methodSubmit",            "openModal",            "noticia"        ],        computed: {        },        created(){            this.retrieveRoles()        },        data() {            return {                openValue: "",                errMsg:  '',                successMsg:  '',                success: false,                isLogin: false,                show: false,                showAlert: false,                showSuccess: false,                updateNoticia: {},                openModalInside: false,                roles: [],                rol_edit: null,                 clearBtn: true,                todayBtn: true,                closeOnSelected: true,                limitFrom: '',                limitTo: '',                format: 'yyyy-MM-dd',                weekStartsWith: 0            }        },        components:{            vSelect        },        watch: {            '$route': 'fetchData',            noticia: function(val, oldVal){                this.openModalInside = this.openModal                this.updateNoticia = this.noticia                            }        },        methods:{            showCallback () {            },            dismissCallback (msg) {                this.showSuccess = false                this.showAlert = false                this.openModalInside =false                this.$emit('openChange', false);            },            retrieveRoles(){                roles.index(this)            },            retrieveData(id) {                noticias.retrieve(this, id)            },            validateMethod(){                console.log(this.methodSubmit)                if(this.methodSubmit == 'update'){                    this.updateUsuario();                }                else{                    console.log(this.methodSubmit)                }            },            updateUsuario() {                this.showAlert = false                this.showSuccess = false                this.$validator.validateAll().then(success => {                    if (success) {                        noticias.update(this, this.updateNoticia)                    }                    else{                        this.errMsg= 'Error revisa el formulario'                        this.showAlert=true                    }                })            }        }    }</script>