<template>    <div>        <modal :size="'md'" :title="title" :footer="false" id="modal-demo-1" ref="modal1" v-model="openModalInside"  @show="showCallback" @hide="dismissCallback">            <div class="wrapper-alert">                <alert type="danger" :closable="true" v-if="showAlert" @close="showAlert=false">                    <h4><i class="icon fa fa-ban"></i> Error!</h4>                    <p>{{ errMsg }}</p>                </alert>                <alert type="success" :closable="true" v-if="showSuccess" @close="showSuccess=false">                    <h4><i class="icon fa fa-check"></i> Exito!</h4>                    <p>{{ successMsg }}</p>                </alert>            </div>            <div class="box box-primary">                            <label>Patrocinador: {{ updatePatrocinador.entidadId.entidad }}</label>                <form @submit.prevent="validateMethod" role="form">                     <div class="box-body">                       <input type="hidden" v-model="id">                         <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('persona') }" >                                <label for="">Nombre Persona</label>                                <input type="text" id="persona" name="persona" data-vv-as="Persona" class="form-control" v-model="updatePatrocinador.personaId.nombre" v-validate="'required'" >                                <span class="help-block" for="persona" v-bind:data-error="errors.first('persona')">                                    {{ errors.first('persona') }}                                </span>                            </div>                        </div>                        <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('entidad') }" >                                <label for="">Nombre Entidad</label>                                <input type="text" id="entidad" name="entidadclub" data-vv-as="Nombre de la entidad" class="form-control" v-model="updatePatrocinador.entidadId.nombre" v-validate="'required'" >                                <span class="help-block" for="entidad" v-bind:data-error="errors.first('entidad')">                                    {{ errors.first('entidad') }}                                </span>                            </div>                        </div>                                                <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('Persona') }" >                                <label for="">Persona</label>                                <input type="text" id="Persona" name="Persona" data-vv-as="Persona " class="form-control" v-model="updatePatrocinador.esPersona" v-validate="'required'" >                                <span class="help-block" for="Persona" v-bind:data-error="errors.first('Persona')">                                    {{ errors.first('Persona') }}                                </span>                            </div>                        </div>                         <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('activo') }" >                                <label for="">Activo</label>                                <input type="text" id="activo" name="activo" data-vv-as="Es Activo" class="form-control" v-model="updatePatrocinador.activo" v-validate="'required'" >                                <span class="help-block" for="activo" v-bind:data-error="errors.first('activo')">                                    {{ errors.first('activo') }}                                </span>                            </div>                        </div>                                                              </div>                        <div class="box-footer">                        <div class="col-xs-12 text-right">                            <button type="submit" class="btn btn-flat btn-sm btn-primary" >{{ buttonMsg }}</button>                            <button type="button" class="btn btn-flat btn-sm btn-primary" @click="dismissCallback()">Cerrar</button>                        </div>                     </div>                </form>            </div>        </modal>    </div></template><script>    import users from '../../../controllers/users.js'    import roles from '../../../controllers/roles.js'    import patrocinadores from '../../../controllers/patrocinador.js'    import moment from 'moment'    import vSelect from "vue-select"    export default {        name: 'ActualizarPatrocinador',        props:  [                        "title",            "id",            "buttonMsg",            "methodSubmit",            "openModal",            "patrocinador"        ],        computed: {        },        created(){            this.retrieveRoles()        },        data() {            return {                openValue: "",                errMsg:  '',                successMsg:  '',                success: false,                isLogin: false,                show: false,                showAlert: false,                showSuccess: false,                updatePatrocinador: {entidadId:' ', personaId: ' ', esPersona: '  ', activo: ' '},                openModalInside: false,                roles: [],                rol_edit: null            }        },        components:{            vSelect        },        watch: {            '$route': 'fetchData',            patrocinador: function(val, oldVal){                this.openModalInside = this.openModal                this.updatePatrocinador = this.patrocinador                            }        },        methods:{            showCallback () {            },            dismissCallback (msg) {                this.showSuccess = false                this.showAlert = false                this.openModalInside =false                this.$emit('openChange', false);            },            retrieveRoles(){                roles.index(this)            },            retrieveData(id) {                patrocinadores.retrieve(this, id)            },            validateMethod(){                console.log(this.methodSubmit)                if(this.methodSubmit == 'update'){                    this.updateUsuario();                }                else{                    console.log(this.methodSubmit)                }            },            updateUsuario() {                this.showAlert = false                this.showSuccess = false                this.$validator.validateAll().then(success => {                    if (success) {                        patrocinadores.update(this, this.updatePatrocinador)                                           }                    else{                        this.errMsg= 'Error revisa el formulario'                        this.showAlert=true                    }                })            }        }    }</script>