<template>    <div>        <modal :size="'md'" :title="title" :footer="false" id="modal-demo-1" ref="modal1" v-model="openModalInside"  @show="showCallback" @hide="dismissCallback">            <div class="wrapper-alert">                <alert type="danger" :closable="true" v-if="showAlert" @close="showAlert=false">                    <h4><i class="icon fa fa-ban"></i> Error!</h4>                    <p>{{ errMsg }}</p>                </alert>                <alert type="success" :closable="true" v-if="showSuccess" @close="showSuccess=false">                    <h4><i class="icon fa fa-check"></i> Exito!</h4>                    <p>{{ successMsg }}</p>                </alert>            </div>            <div class="box box-primary" >                            <label>Patrocinio de: {{ updatePatrocinadorAtleta.patrocinadorId.nombre }}</label>                <form @submit.prevent="validateMethod" role="form">                     <div class="box-body">                       <input type="hidden" v-model="id">                                                                        <div class="col-xs-12 col-sm-6">                                        <div class="fgroup required"  :class="{ 'has-error': errors.has('patrocinadorId') }">                                            <label for="">Patrocinador</label>                                            <v-select                                                :debounce="250"                                                :options="patrocinadors"                                                v-model="updatePatrocinadorAtleta.patrocinadorId"                                                placeholder="Seleccione un Patrocinador"                                                label="nombre">                                            </v-select>                                            <div class="clearfix"></div>                                            <input type="hidden" id="patrocinadorId" name="patrocinadorId" data-vv-as="patrocinadorId " class="form-control" v-model="updatePatrocinadorAtleta.patrocinadorId" v-validate="'required'" >                                            <span class="help-block" for="patrocinadorId" v-bind:data-error="errors.first('patrocinadorId')">                                                {{ errors.first('patrocinadorId') }}                                            </span>                                        </div>                                    </div>                                    <div class="col-xs-12 col-sm-6">                                        <div class="fgroup required"  :class="{ 'has-error': errors.has('atletaId') }">                                            <label for="">Atleta Patrocinado</label>                                            <v-select                                                :debounce="250"                                                :options="atletas"                                                v-model="updatePatrocinadorAtleta.atletaId"                                                placeholder="Seleccione un Atleta"                                                label="nombre">                                            </v-select>                                            <div class="clearfix"></div>                                            <input type="hidden" id="atletaId" name="atletaId" data-vv-as="atletaId " class="form-control" v-model="updatePatrocinadorAtleta.atletaId" v-validate="'required'" >                                            <span class="help-block" for="atletaId" v-bind:data-error="errors.first('atletaId')">                                                {{ errors.first('atletaId') }}                                            </span>                                        </div>                                    </div>                                     <div class="col-xs-12 col-sm-6">                            <div class="fgroup required" :class="{ 'has-error': errors.has('tiempoPatrocinio') }" >                                <label for="">Tiempo de Patrocinio (AÃ±os)</label>                                <input type="number" id="tiempoPatrocinio" name="tiempoPatrocinio" data-vv-as="Tiempo de Patrocinio" class="form-control"                                 v-model="updatePatrocinadorAtleta.tiempoPatrocinio" v-validate="'required|min_value:1|max_value:1000'" >                                <span class="help-block" for="tiempoPatrocinio" v-bind:data-error="errors.first('tiempoPatrocinio')">                                    {{ errors.first('tiempoPatrocinio') }}                                </span>                            </div>                        </div>                        <div class="col-xs-12 text-right">                            <button type="submit" class="btn btn-flat btn-sm btn-primary">{{ buttonMsg }}</button>                        </div>                    </div>                </form>            </div>        </modal>    </div></template><script>    import users from '../../../controllers/users.js'    import roles from '../../../controllers/roles.js'    import patrocinador_atleta from '../../../controllers/patrocinador_atleta.js'    import patrocinadors from '../../../controllers/patrocinadores.js'    import atleta from '../../../controllers/atletas.js'    import moment from 'moment'    import vSelect from "vue-select"    export default {        name: 'ActualizarPatrocinadorAtleta',        props:  [                        "title",            "id",            "buttonMsg",            "methodSubmit",            "openModal",            "patrocinadorAtleta"        ],        computed: {        },        created(){            this.retrieveRoles()            patrocinadors.index(this)                 atleta.index(this)         },        data() {            return {                openValue: "",                errMsg:  '',                successMsg:  '',                success: false,                isLogin: false,                show: false,                showAlert: false,                showSuccess: false,                updatePatrocinadorAtleta: {patrocinadorId:''},                openModalInside: false,                roles: [],                rol_edit: null,                patrocinadors: [],                atletas: []            }        },        components:{            vSelect        },        watch: {            '$route': 'fetchData',            patrocinadorAtleta: function(val, oldVal){                this.openModalInside = this.openModal                this.updatePatrocinadorAtleta = this.patrocinadorAtleta                            }        },        methods:{            fetchData(){                patrocinadorController.index(this)                atleta.index(this)            },            showCallback () {            },            dismissCallback (msg) {                this.showSuccess = false                this.showAlert = false                this.openModalInside =false                this.$emit('openChange', false);            },            retrieveRoles(){                roles.index(this)            },            retrieveData(id) {                patrocinador_atleta.retrieve(this, id)            },            validateMethod(){                console.log(this.methodSubmit)                if(this.methodSubmit == 'update'){                    this.updateUsuario();                }                else{                    console.log(this.methodSubmit)                }            },            updateUsuario() {                this.showAlert = false                this.showSuccess = false                this.$validator.validateAll().then(success => {                    if (success) {                        patrocinador_atleta.update(this, this.updatePatrocinadorAtleta)                    }                    else{                        this.errMsg= 'Error revisa el formulario'                        this.showAlert=true                    }                })            }        }    }</script>