<template>    <div>        <modal :size="'md'" :title="title" :footer="false" id="modal-demo-1" ref="modal1" v-model="open1"  @show="showCallback" @hide="dismissCallback">            <div class="wrapper-alert">                <alert type="danger" :closable="true" v-if="showAlert" @close="showAlert=false">                    <h4><i class="icon fa fa-ban"></i> Alert!</h4>                    <p>{{ errMsg }}</p>                </alert>                <alert type="success" :closable="true" v-if="showSuccess" @close="showSuccess=false">                    <h4><i class="icon fa fa-check"></i> Success!</h4>                    <p>{{ successMsg }}</p>                </alert>            </div>            <div class="box box-primary">                                    <form @submit.prevent="validateMethod" role="form">                     <div class="box-body">                        <input type="hidden" v-model="id">                        <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('usuario') }" >                                <label for="">Usuario</label>                                <input type="text" id="usuario" name="usuario" data-vv-as="First Name" class="form-control" v-model="usuario" v-validate:usuario="'required'" >                                <span class="help-block" for="usuario" v-bind:data-error="errors.first('usuario')">                                    {{ errors.first('usuario') }}                                </span>                            </div>                        </div>                                            </div>                        <div class="box-footer">                        <div class="col-xs-12 text-right">                            <button type="submit" class="btn btn-flat btn-sm btn-primary">{{ buttonMsg }}</button>                        </div>                    </div>                </form>            </div>        </modal>    </div></template><script>    import users from '../../../controllers/users.js'    import moment from 'moment'    export default {        name: 'Employee',        props:  [            "usuario",            "title",            "usuario",            "contrasena",            "id",            "buttonMsg",            "methodSubmit",            "open1"        ],        computed: {            listJson: function(){                return JSON.parse(this.id, this.birth_date);            },        },        created(){            this.open1 = this.open1        },        data() {            return {                openValue: "",                errMsg:  '',                successMsg:  '',                success: false,                isLogin: false,                show: false,                showAlert: false,                showSuccess: false            }        },        watch: {            '$route': 'fetchData',            birth_date: function(val, oldVal){                this.birth_date = moment(String(this.birth_date)).format('YYYY-MM-DD');            }        },        methods:{            showCallback () {            },            dismissCallback (msg) {                this.$emit('openChange', false);            },            retrieveData(id) {                users.retrieve(this, id)            },            validateMethod(){                console.log(this.methodSubmit)                if(this.methodSubmit == 'update'){                    this.updateEmployee();                }                else{                    console.log(this.methodSubmit)                }            },            updateEmployee() {                this.showAlert = false                this.showSuccess = false                this.$validator.validateAll().then(success => {                    if (success) {                        var usersParams = {                            usuario: this.usuario,                            id: this.id,                                                    }                        users.update(this, usersParams)                    }                    else{                        this.errMsg= 'Error'                        this.showAlert=true                    }                })            }        }    }</script>