<template>    <div>        <modal :size="'md'" :title="title" :footer="false" id="modal-demo-1" ref="modal1" v-model="openModalInside"  @show="showCallback" @hide="dismissCallback">            <div class="wrapper-alert">                <alert type="danger" :closable="true" v-if="showAlert" @close="showAlert=false">                    <h4><i class="icon fa fa-ban"></i> Error!</h4>                    <p>{{ errMsg }}</p>                </alert>                <alert type="success" :closable="true" v-if="showSuccess" @close="showSuccess=false">                    <h4><i class="icon fa fa-check"></i> Exito!</h4>                    <p>{{ successMsg }}</p>                </alert>            </div>            <div class="box box-primary">                            <label>Usuario: {{ updateUser.usuario }}</label>                <form @submit.prevent="validateMethod" role="form">                     <div class="box-body">                        <input type="hidden" v-model="id">                        <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('password') }">                                <label for="">Contraseña</label>                                <input type="password" id="password" name="password" class="form-control" data-vv-as="Contraseña"  v-model="updateUser.contrasena" v-validate="'required|confirmed:confirm_password|min:8'" >                                <span class="help-block" for="password" v-bind:data-error="errors.first('password')">                                    {{ errors.first('password') }}                                </span>                            </div>                        </div>                        <div class="col-xs-12 col-sm-6">                            <div class="fgroup" :class="{ 'has-error': errors.has('confirm_password') }">                                <label for="">Confirmación de contraseña</label>                                <input type="password" id="confirm_password" name="confirm_password" class="form-control" data-vv-as="Confirmación de contraseña"  v-model="confirm_password" v-validate="'required'" >                                <span class="help-block" for="confirm_password" v-bind:data-error="errors.first('confirm_password')">                                    {{ errors.first('confirm_password') }}                                </span>                            </div>                        </div>                    </div>                        <div class="box-footer">                        <div class="col-xs-12 text-right">                            <button type="submit" class="btn btn-flat btn-sm btn-primary">{{ buttonMsg }}</button>                        </div>                    </div>                </form>            </div>        </modal>    </div></template><script>    import users from '../../../controllers/users.js'    import roles from '../../../controllers/roles.js'    import moment from 'moment'    import vSelect from "vue-select"    export default {        name: 'Employee',        props:  [            "usuario",            "title",            "usuario",            "contrasena",            "id",            "buttonMsg",            "methodSubmit",            "openModal",        ],        computed: {        },        created(){            this.retrieveRoles()        },        data() {            return {                confirm_password: '',                openValue: "",                errMsg:  '',                successMsg:  '',                success: false,                isLogin: false,                show: false,                showAlert: false,                showSuccess: false,                updateUser: {},                openModalInside: false,                roles: [],                rol_edit: null            }        },        components:{            vSelect        },        watch: {            '$route': 'fetchData',            usuario: function(val, oldVal){                this.openModalInside = this.openModal                this.updateUser = this.usuario                this.updateUser.contrasena = ""                this.confirm_password = ""                this.updateUser.rolId = this.usuario.rolId                this.errors.clear()            }        },        methods:{            showCallback () {            },            dismissCallback (msg) {                this.showSuccess = false                this.showAlert = false                this.openModalInside =false                this.$emit('openChange', false);            },            retrieveRoles(){                roles.index(this)            },            retrieveData(id) {                users.retrieve(this, id)            },            validateMethod(){                console.log(this.methodSubmit)                if(this.methodSubmit == 'update'){                    this.updateUsuario();                }                else{                    console.log(this.methodSubmit)                }            },            updateUsuario() {                this.showAlert = false                this.showSuccess = false                this.$validator.validateAll().then(success => {                    if (success) {                        users.update(this, this.updateUser)                    }                    else{                        this.errMsg = 'Error revisa el formulario'                        this.showAlert=true                    }                })            }        }    }</script>